mapper.delete:
DELETE FROM t_psi_appraisal_record
WHERE

       AND appraisal_id = #{appraisalId, jdbcType=BIGINT}

DELETE FROM t_psi_appraisal_record
WHERE


      AND appraisal_id = #{appraisalId, jdbcType=BIGINT}


      AND inquiry_id = #{inquiryId, jdbcType=BIGINT}


      AND appraisal_price = #{appraisalPrice, jdbcType=BIGINT}


      AND appraisal_staff_id = #{appraisalStaffId, jdbcType=BIGINT}


      AND appraisal_date = #{appraisalDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_psi_appraisal_record a


      AND a.appraisal_id = #{appraisalId, jdbcType=BIGINT}


      AND a.inquiry_id = #{inquiryId, jdbcType=BIGINT}


      AND a.appraisal_price = #{appraisalPrice, jdbcType=BIGINT}


      AND a.appraisal_staff_id = #{appraisalStaffId, jdbcType=BIGINT}


      AND a.appraisal_date = #{appraisalDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_psi_appraisal_record a


      AND a.appraisal_id = #{appraisalId, jdbcType=BIGINT}


      AND a.inquiry_id = #{inquiryId, jdbcType=BIGINT}


      AND a.appraisal_price = #{appraisalPrice, jdbcType=BIGINT}


      AND a.appraisal_staff_id = #{appraisalStaffId, jdbcType=BIGINT}


      AND a.appraisal_date = #{appraisalDate, jdbcType=TIMESTAMP}
