mapper.delete:
DELETE FROM t_psi_business_nodes_material
WHERE

       AND id = #{id, jdbcType=BIGINT}

DELETE FROM t_psi_business_nodes_material
WHERE


      AND id = #{id, jdbcType=BIGINT}


      AND business_nodes = #{businessNodes, jdbcType=VARCHAR}


      AND business_nodes_name = #{businessNodesName, jdbcType=VARCHAR}


      AND material_type = #{materialType, jdbcType=VARCHAR}


      AND material_type_name = #{materialTypeName, jdbcType=VARCHAR}


      AND material_sequence = #{materialSequence, jdbcType=INTEGER}


      AND material_count = #{materialCount, jdbcType=INTEGER}


      AND is_upload = #{isUpload, jdbcType=INTEGER}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

