mapper.delete:
DELETE FROM t_psi_car_acquisition
WHERE

       AND reservation_id = #{reservationId, jdbcType=BIGINT}

DELETE FROM t_psi_car_acquisition
WHERE


      AND reservation_id = #{reservationId, jdbcType=BIGINT}


      AND other_brand_inquiry_id = #{otherBrandInquiryId, jdbcType=BIGINT}


      AND customer_intention = #{customerIntention, jdbcType=VARCHAR}


      AND estimate_deal_price = #{estimateDealPrice, jdbcType=BIGINT}


      AND invoice_num = #{invoiceNum, jdbcType=VARCHAR}


      AND deal_price_end = #{dealPriceEnd, jdbcType=BIGINT}


      AND pay_type = #{payType, jdbcType=VARCHAR}


      AND material_status = #{materialStatus, jdbcType=VARCHAR}


      AND acquisition_status = #{acquisitionStatus, jdbcType=VARCHAR}


      AND approval_status = #{approvalStatus, jdbcType=VARCHAR}


      AND acquisition_abandoned_reason = #{acquisitionAbandonedReason, jdbcType=VARCHAR}


      AND estimated_transfer_time = #{estimatedTransferTime, jdbcType=TIMESTAMP}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND oneself_brand_inquiryid = #{oneselfBrandInquiryid, jdbcType=BIGINT}


      AND acquirer_id = #{acquirerId, jdbcType=BIGINT}


      AND storage_id = #{storageId, jdbcType=BIGINT}

