      AND cost_info_id = #{costInfoId, jdbcType=BIGINT}


      AND stock_id = #{stockId, jdbcType=BIGINT}


      AND checkout_date = #{checkoutDate, jdbcType=TIMESTAMP}


      AND income_price = #{incomePrice, jdbcType=DECIMAL}


      AND service_cost = #{serviceCost, jdbcType=DECIMAL}


      AND facial_cost = #{facialCost, jdbcType=DECIMAL}


      AND logistics_cost = #{logisticsCost, jdbcType=DECIMAL}


      AND test_cost = #{testCost, jdbcType=DECIMAL}


      AND retain_cost = #{retainCost, jdbcType=VARCHAR}


      AND friendly_cost = #{friendlyCost, jdbcType=DECIMAL}


      AND right_cost = #{rightCost, jdbcType=DECIMAL}


      AND interest_cost = #{interestCost, jdbcType=DECIMAL}


      AND gift_cost = #{giftCost, jdbcType=DECIMAL}


      AND brokerage_cost = #{brokerageCost, jdbcType=DECIMAL}


      AND other_cost = #{otherCost, jdbcType=DECIMAL}


      AND sell_price = #{sellPrice, jdbcType=DECIMAL}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


