mapper.delete:
DELETE FROM t_psi_car_sale_record
WHERE

       AND sale_record_id = #{saleRecordId, jdbcType=BIGINT}

DELETE FROM t_psi_car_sale_record
WHERE


      AND sale_record_id = #{saleRecordId, jdbcType=BIGINT}


      AND stock_id = #{stockId, jdbcType=BIGINT}


      AND vin_code = #{vinCode, jdbcType=VARCHAR}


      AND partner_name = #{partnerName, jdbcType=VARCHAR}


      AND deliver_date = #{deliverDate, jdbcType=TIMESTAMP}


      AND remark = #{remark, jdbcType=VARCHAR}


      AND wholesale_price = #{wholesalePrice, jdbcType=DECIMAL}


      AND wholesale_no = #{wholesaleNo, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

