mapper.delete:
DELETE FROM t_psi_car_sale_record_file
WHERE

       AND record_file_id = #{recordFileId, jdbcType=BIGINT}

DELETE FROM t_psi_car_sale_record_file
WHERE


      AND record_file_id = #{recordFileId, jdbcType=BIGINT}


      AND sale_record_id = #{saleRecordId, jdbcType=BIGINT}


      AND file_id = #{fileId, jdbcType=BIGINT}


      AND file_order = #{fileOrder, jdbcType=INTEGER}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND thumbnail = #{thumbnail, jdbcType=LONGVARCHAR}

mapper.select:
SELECT 

  FROM t_psi_car_sale_record_file a


