mapper.delete:
DELETE FROM t_psi_car_service_info
WHERE

       AND service_id = #{serviceId, jdbcType=BIGINT}

DELETE FROM t_psi_car_service_info
WHERE


      AND service_id = #{serviceId, jdbcType=BIGINT}


      AND stock_id = #{stockId, jdbcType=BIGINT}


      AND service_place = #{servicePlace, jdbcType=VARCHAR}


      AND service_place_code = #{servicePlaceCode, jdbcType=VARCHAR}


      AND place_type = #{placeType, jdbcType=VARCHAR}


      AND place_type_code = #{placeTypeCode, jdbcType=VARCHAR}


      AND plan_start_date = #{planStartDate, jdbcType=TIMESTAMP}


      AND plan_end_date = #{planEndDate, jdbcType=TIMESTAMP}


      AND estimated_delivery_time = #{estimatedDeliveryTime, jdbcType=TIMESTAMP}


      AND service_number = #{serviceNumber, jdbcType=VARCHAR}


      AND start_date = #{startDate, jdbcType=TIMESTAMP}


      AND expect_end_time = #{expectEndTime, jdbcType=TIMESTAMP}


      AND start_people_id = #{startPeopleId, jdbcType=BIGINT}


      AND start_people = #{startPeople, jdbcType=VARCHAR}


      AND feedback_date = #{feedbackDate, jdbcType=TIMESTAMP}


      AND plan_repair_start_date = #{planRepairStartDate, jdbcType=TIMESTAMP}


      AND plan_repair_end_date = #{planRepairEndDate, jdbcType=TIMESTAMP}


      AND service_state = #{serviceState, jdbcType=VARCHAR}


      AND total_price = #{totalPrice, jdbcType=DECIMAL}


      AND quote_order_no = #{quoteOrderNo, jdbcType=VARCHAR}


      AND plan_complete_date = #{planCompleteDate, jdbcType=TIMESTAMP}


      AND complete_date = #{completeDate, jdbcType=TIMESTAMP}


      AND warehous_date = #{warehousDate, jdbcType=TIMESTAMP}


      AND warehous_people_id = #{warehousPeopleId, jdbcType=BIGINT}


      AND approval_result = #{approvalResult, jdbcType=VARCHAR}


      AND approval_date = #{approvalDate, jdbcType=TIMESTAMP}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_psi_car_service_info a


      AND a.service_id = #{serviceId, jdbcType=BIGINT}


      AND a.stock_id = #{stockId, jdbcType=BIGINT}


      AND a.service_place = #{servicePlace, jdbcType=VARCHAR}


      AND a.service_place_code = #{servicePlaceCode, jdbcType=VARCHAR}


      AND a.place_type = #{placeType, jdbcType=VARCHAR}


      AND a.place_type_code = #{placeTypeCode, jdbcType=VARCHAR}


      AND a.plan_start_date = #{planStartDate, jdbcType=TIMESTAMP}


      AND a.plan_end_date = #{planEndDate, jdbcType=TIMESTAMP}


      AND a.estimated_delivery_time = #{estimatedDeliveryTime, jdbcType=TIMESTAMP}


      AND a.service_number = #{serviceNumber, jdbcType=VARCHAR}


      AND a.start_date = #{startDate, jdbcType=TIMESTAMP}


      AND a.expect_end_time = #{expectEndTime, jdbcType=TIMESTAMP}


      AND a.start_people_id = #{startPeopleId, jdbcType=BIGINT}


      AND a.start_people = #{startPeople, jdbcType=VARCHAR}


      AND a.feedback_date = #{feedbackDate, jdbcType=TIMESTAMP}


      AND a.plan_repair_start_date = #{planRepairStartDate, jdbcType=TIMESTAMP}


      AND a.plan_repair_end_date = #{planRepairEndDate, jdbcType=TIMESTAMP}


      AND a.service_state = #{serviceState, jdbcType=VARCHAR}


      AND a.total_price = #{totalPrice, jdbcType=DECIMAL}


      AND a.quote_order_no = #{quoteOrderNo, jdbcType=VARCHAR}


      AND a.plan_complete_date = #{planCompleteDate, jdbcType=TIMESTAMP}


      AND a.complete_date = #{completeDate, jdbcType=TIMESTAMP}


      AND a.warehous_date = #{warehousDate, jdbcType=TIMESTAMP}


      AND a.warehous_people_id = #{warehousPeopleId, jdbcType=BIGINT}


      AND a.approval_result = #{approvalResult, jdbcType=VARCHAR}


      AND a.approval_date = #{approvalDate, jdbcType=TIMESTAMP}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_enable = #{isEnable, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}

    order by a.created_date desc

