mapper.delete:
DELETE FROM t_psi_car_service_material_file
WHERE

       AND material_file_id = #{materialFileId, jdbcType=BIGINT}

DELETE FROM t_psi_car_service_material_file
WHERE


      AND material_file_id = #{materialFileId, jdbcType=BIGINT}


      AND material_id = #{materialId, jdbcType=BIGINT}


      AND material_file_type = #{materialFileType, jdbcType=VARCHAR}


      AND file_sort = #{fileSort, jdbcType=TINYINT}


      AND approval_people_id = #{approvalPeopleId, jdbcType=BIGINT}


      AND approval_date = #{approvalDate, jdbcType=TIMESTAMP}


      AND reject_reason = #{rejectReason, jdbcType=VARCHAR}


      AND chinese_description = #{chineseDescription, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}


      AND thumbnail = #{thumbnail, jdbcType=LONGVARCHAR}

