mapper.delete:
DELETE FROM t_psi_car_service_repair_project
WHERE

       AND project_id = #{projectId, jdbcType=BIGINT}

DELETE FROM t_psi_car_service_repair_project
WHERE


      AND project_id = #{projectId, jdbcType=BIGINT}


      AND service_id = #{serviceId, jdbcType=BIGINT}


      AND repair_project_type = #{repairProjectType, jdbcType=VARCHAR}


      AND repair_project_code = #{repairProjectCode, jdbcType=VARCHAR}


      AND repair_project_name = #{repairProjectName, jdbcType=VARCHAR}


      AND time_price = #{timePrice, jdbcType=DECIMAL}


      AND is_component = #{isComponent, jdbcType=TINYINT}


      AND is_repair = #{isRepair, jdbcType=TINYINT}


      AND change_operate = #{changeOperate, jdbcType=TINYINT}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_psi_car_service_repair_project a


