      AND a.repair_project_type = #{repairProjectType, jdbcType=VARCHAR}


      AND a.repair_project_code = #{repairProjectCode, jdbcType=VARCHAR}


      AND a.repair_project_name = #{repairProjectName, jdbcType=VARCHAR}


      AND a.time_price = #{timePrice, jdbcType=DECIMAL}


      AND a.is_component = #{isComponent, jdbcType=TINYINT}


      AND a.is_repair = #{isRepair, jdbcType=TINYINT}


      AND a.change_operate = #{changeOperate, jdbcType=TINYINT}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_enable = #{isEnable, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}

