mapper.delete:
DELETE FROM t_psi_car_stock_history
WHERE

       AND history_id = #{historyId, jdbcType=BIGINT}

DELETE FROM t_psi_car_stock_history
WHERE


      AND history_id = #{historyId, jdbcType=BIGINT}


      AND stock_id = #{stockId, jdbcType=BIGINT}


      AND storage_name = #{storageName, jdbcType=VARCHAR}


      AND stock_type = #{stockType, jdbcType=VARCHAR}


      AND stock_date = #{stockDate, jdbcType=TIMESTAMP}


      AND create_name = #{createName, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

