mapper.delete:
DELETE FROM t_psi_clues_follow_record
WHERE

       AND follow_id = #{followId, jdbcType=BIGINT}

DELETE FROM t_psi_clues_follow_record
WHERE


      AND follow_id = #{followId, jdbcType=BIGINT}


      AND clues_id = #{cluesId, jdbcType=BIGINT}


      AND follow_time = #{followTime, jdbcType=TIMESTAMP}


      AND follow_info = #{followInfo, jdbcType=VARCHAR}


      AND last_follow_time = #{lastFollowTime, jdbcType=TIMESTAMP}


      AND follow_type = #{followType, jdbcType=VARCHAR}


      AND is_arrival = #{isArrival, jdbcType=VARCHAR}


      AND follow_status = #{followStatus, jdbcType=VARCHAR}


      AND follow_person = #{followPerson, jdbcType=BIGINT}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_psi_clues_follow_record a


      AND a.follow_id = #{followId, jdbcType=BIGINT}


      AND a.clues_id = #{cluesId, jdbcType=BIGINT}


      AND a.follow_time = #{followTime, jdbcType=TIMESTAMP}


      AND a.follow_info = #{followInfo, jdbcType=VARCHAR}


      AND a.last_follow_time = #{lastFollowTime, jdbcType=TIMESTAMP}


      AND a.follow_type = #{followType, jdbcType=VARCHAR}


      AND a.is_arrival = #{isArrival, jdbcType=VARCHAR}


      AND a.follow_status = #{followStatus, jdbcType=VARCHAR}


      AND a.follow_person = #{followPerson, jdbcType=BIGINT}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_enable = #{isEnable, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}


  order by follow_time desc

