mapper.delete:
DELETE FROM t_psi_customer_basic_information
WHERE

       AND customer_id = #{customerId, jdbcType=BIGINT}

DELETE FROM t_psi_customer_basic_information
WHERE


      AND customer_id = #{customerId, jdbcType=BIGINT}


      AND uid = #{uid, jdbcType=VARCHAR}


      AND sid = #{sid, jdbcType=VARCHAR}


      AND clues_type = #{cluesType, jdbcType=VARCHAR}


      AND clues_source = #{cluesSource, jdbcType=VARCHAR}


      AND creation_time = #{creationTime, jdbcType=TIMESTAMP}


      AND clues_order_no = #{cluesOrderNo, jdbcType=VARCHAR}


      AND customer_name = #{customerName, jdbcType=VARCHAR}


      AND customer_iphone = #{customerIphone, jdbcType=VARCHAR}


      AND clues_status = #{cluesStatus, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

