mapper.delete:
DELETE FROM t_psi_customer_booking_tracking
WHERE

       AND booking_id = #{bookingId, jdbcType=BIGINT}

DELETE FROM t_psi_customer_booking_tracking
WHERE


      AND booking_id = #{bookingId, jdbcType=BIGINT}


      AND clues_id = #{cluesId, jdbcType=BIGINT}


      AND customer_id = #{customerId, jdbcType=BIGINT}


      AND store_id = #{storeId, jdbcType=BIGINT}


      AND adviser_id = #{adviserId, jdbcType=BIGINT}


      AND driving_file_id = #{drivingFileId, jdbcType=BIGINT}


      AND business_type = #{businessType, jdbcType=VARCHAR}


      AND business_number = #{businessNumber, jdbcType=VARCHAR}


      AND scheduled_test_time = #{scheduledTestTime, jdbcType=TIMESTAMP}


      AND scheduled_test_address = #{scheduledTestAddress, jdbcType=VARCHAR}


      AND customer_expected_price = #{customerExpectedPrice, jdbcType=VARCHAR}


      AND receiving_orders_deadline = #{receivingOrdersDeadline, jdbcType=TIMESTAMP}


      AND track_status = #{trackStatus, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

