mapper.delete:
DELETE FROM t_psi_customer_reservation_track
WHERE

       AND reservation_id = #{reservationId, jdbcType=BIGINT}

DELETE FROM t_psi_customer_reservation_track
WHERE


      AND reservation_id = #{reservationId, jdbcType=BIGINT}


      AND info_id = #{infoId, jdbcType=BIGINT}


      AND customer_id = #{customerId, jdbcType=BIGINT}


      AND store_id = #{storeId, jdbcType=BIGINT}


      AND consultant_id = #{consultantId, jdbcType=BIGINT}


      AND manager_id = #{managerId, jdbcType=BIGINT}


      AND store_manager_id = #{storeManagerId, jdbcType=BIGINT}


      AND make_order_person_name = #{makeOrderPersonName, jdbcType=VARCHAR}


      AND make_order_person_iphone = #{makeOrderPersonIphone, jdbcType=VARCHAR}


      AND make_order_person_role = #{makeOrderPersonRole, jdbcType=VARCHAR}


      AND quote_merchants_name = #{quoteMerchantsName, jdbcType=VARCHAR}


      AND business_type = #{businessType, jdbcType=VARCHAR}


      AND business_order_no = #{businessOrderNo, jdbcType=VARCHAR}


      AND business_classify = #{businessClassify, jdbcType=VARCHAR}


      AND agree_describe = #{agreeDescribe, jdbcType=VARCHAR}


      AND order_receiving_deadline = #{orderReceivingDeadline, jdbcType=TIMESTAMP}


      AND quote_deadline = #{quoteDeadline, jdbcType=TIMESTAMP}


      AND other_brand_inquiry_id = #{otherBrandInquiryId, jdbcType=BIGINT}


      AND oneself_brand_inquiry_id = #{oneselfBrandInquiryId, jdbcType=BIGINT}


      AND estimate_deal_price = #{estimateDealPrice, jdbcType=BIGINT}


      AND deal_price_end = #{dealPriceEnd, jdbcType=BIGINT}


      AND customer_intention = #{customerIntention, jdbcType=VARCHAR}


      AND shut_cause = #{shutCause, jdbcType=VARCHAR}


      AND shut_cause_details = #{shutCauseDetails, jdbcType=VARCHAR}


      AND shut_describe = #{shutDescribe, jdbcType=VARCHAR}


      AND shut_date = #{shutDate, jdbcType=TIMESTAMP}


      AND track_status = #{trackStatus, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

