mapper.delete:
DELETE FROM t_psi_customer_vehicle
WHERE

       AND vehicle_id = #{vehicleId, jdbcType=BIGINT}

DELETE FROM t_psi_customer_vehicle
WHERE


      AND vehicle_id = #{vehicleId, jdbcType=BIGINT}


      AND booking_id = #{bookingId, jdbcType=BIGINT}


      AND customer_id = #{customerId, jdbcType=VARCHAR}


      AND brand = #{brand, jdbcType=VARCHAR}


      AND car_type = #{carType, jdbcType=VARCHAR}


      AND registration_time = #{registrationTime, jdbcType=DATE}


      AND driver_mileage = #{driverMileage, jdbcType=VARCHAR}


      AND car_color = #{carColor, jdbcType=VARCHAR}


      AND car_card_number = #{carCardNumber, jdbcType=VARCHAR}


      AND vin_code = #{vinCode, jdbcType=VARCHAR}


      AND transfer_ownership_times = #{transferOwnershipTimes, jdbcType=VARCHAR}


      AND using_nature = #{usingNature, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

