mapper.delete:
DELETE FROM t_psi_mainteance_items
WHERE

       AND id = #{id, jdbcType=BIGINT}

DELETE FROM t_psi_mainteance_items
WHERE


      AND id = #{id, jdbcType=BIGINT}


      AND stock_id = #{stockId, jdbcType=BIGINT}


      AND vin_code = #{vinCode, jdbcType=VARCHAR}


      AND project_no = #{projectNo, jdbcType=VARCHAR}


      AND project_name = #{projectName, jdbcType=VARCHAR}


      AND collect_fees = #{collectFees, jdbcType=VARCHAR}


      AND maintain_time = #{maintainTime, jdbcType=TIMESTAMP}


      AND maintain_type = #{maintainType, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND update_by = #{updateBy, jdbcType=BIGINT}


      AND update_date = #{updateDate, jdbcType=TIMESTAMP}

