mapper.delete:
DELETE FROM t_psi_message_info
WHERE

       AND message_id = #{messageId, jdbcType=BIGINT}

DELETE FROM t_psi_message_info
WHERE


      AND message_id = #{messageId, jdbcType=BIGINT}


      AND message_type = #{messageType, jdbcType=VARCHAR}


      AND template_id = #{templateId, jdbcType=VARCHAR}


      AND reservation_id = #{reservationId, jdbcType=BIGINT}


      AND Recipient = #{recipient, jdbcType=VARCHAR}


      AND status = #{status, jdbcType=VARCHAR}


      AND reception_time = #{receptionTime, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_psi_message_info a


      AND a.message_id = #{messageId, jdbcType=BIGINT}


      AND a.message_type = #{messageType, jdbcType=VARCHAR}


      AND a.template_id = #{templateId, jdbcType=VARCHAR}


      AND a.reservation_id = #{reservationId, jdbcType=BIGINT}


      AND a.Recipient = #{recipient, jdbcType=VARCHAR}


      AND a.status = #{status, jdbcType=VARCHAR}


      AND a.reception_time = #{receptionTime, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_psi_message_info a


      AND a.message_id = #{messageId, jdbcType=BIGINT}


      AND a.message_type = #{messageType, jdbcType=VARCHAR}


      AND a.template_id = #{templateId, jdbcType=VARCHAR}


      AND a.reservation_id = #{reservationId, jdbcType=BIGINT}


      AND a.Recipient = #{recipient, jdbcType=VARCHAR}


      AND a.status = #{status, jdbcType=VARCHAR}


      AND a.reception_time = #{receptionTime, jdbcType=TIMESTAMP}
