mapper.delete:
DELETE FROM t_psi_new_car_order
WHERE

       AND new_car_id = #{newCarId, jdbcType=BIGINT}

DELETE FROM t_psi_new_car_order
WHERE


      AND new_car_id = #{newCarId, jdbcType=BIGINT}


      AND reservation_id = #{reservationId, jdbcType=BIGINT}


      AND new_car_models = #{newCarModels, jdbcType=VARCHAR}


      AND new_car_vin = #{newCarVin, jdbcType=VARCHAR}


      AND new_car_invoice_no = #{newCarInvoiceNo, jdbcType=VARCHAR}


      AND new_car_order_no = #{newCarOrderNo, jdbcType=VARCHAR}


      AND new_car_delivery_date = #{newCarDeliveryDate, jdbcType=DATE}


      AND order_status = #{orderStatus, jdbcType=VARCHAR}


      AND owner_name = #{ownerName, jdbcType=VARCHAR}


      AND owner_phone = #{ownerPhone, jdbcType=VARCHAR}


      AND main_user_name = #{mainUserName, jdbcType=VARCHAR}


      AND main_user_phone = #{mainUserPhone, jdbcType=VARCHAR}


      AND car_series_code = #{carSeriesCode, jdbcType=VARCHAR}


      AND car_series = #{carSeries, jdbcType=VARCHAR}


      AND car_type_code = #{carTypeCode, jdbcType=VARCHAR}


      AND car_type = #{carType, jdbcType=VARCHAR}


      AND owner_card_no = #{ownerCardNo, jdbcType=VARCHAR}


      AND order_deliver_status = #{orderDeliverStatus, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

