mapper.delete:
DELETE FROM t_psi_pricing_journal
WHERE

       AND journal_id = #{journalId, jdbcType=BIGINT}

DELETE FROM t_psi_pricing_journal
WHERE


      AND journal_id = #{journalId, jdbcType=BIGINT}


      AND vhcl_id = #{vhclId, jdbcType=BIGINT}


      AND pricing_amount = #{pricingAmount, jdbcType=DECIMAL}


      AND create_name = #{createName, jdbcType=VARCHAR}


      AND create_by = #{createBy, jdbcType=BIGINT}


      AND create_date = #{createDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_psi_pricing_journal a


      AND a.journal_id = #{journalId, jdbcType=BIGINT}


      AND a.vhcl_id = #{vhclId, jdbcType=BIGINT}


      AND a.pricing_amount = #{pricingAmount, jdbcType=DECIMAL}


      AND a.create_name = #{createName, jdbcType=VARCHAR}


      AND a.create_by = #{createBy, jdbcType=BIGINT}


      AND a.create_date = #{createDate, jdbcType=TIMESTAMP}


  order by a.create_date desc

SELECT count(1) FROM t_psi_pricing_journal a


      AND a.journal_id = #{journalId, jdbcType=BIGINT}


      AND a.vhcl_id = #{vhclId, jdbcType=BIGINT}


      AND a.pricing_amount = #{pricingAmount, jdbcType=DECIMAL}


      AND a.create_name = #{createName, jdbcType=VARCHAR}


      AND a.create_by = #{createBy, jdbcType=BIGINT}


      AND a.create_date = #{createDate, jdbcType=TIMESTAMP}
