mapper.delete:
DELETE FROM t_psi_retention_clues
WHERE

       AND clues_id = #{cluesId, jdbcType=BIGINT}

DELETE FROM t_psi_retention_clues
WHERE


      AND clues_id = #{cluesId, jdbcType=BIGINT}


      AND storage_id = #{storageId, jdbcType=BIGINT}


      AND stock_id = #{stockId, jdbcType=BIGINT}


      AND customer_id = #{customerId, jdbcType=BIGINT}


      AND model_code = #{modelCode, jdbcType=VARCHAR}


      AND model_name = #{modelName, jdbcType=VARCHAR}


      AND shape_code = #{shapeCode, jdbcType=VARCHAR}


      AND shape_name = #{shapeName, jdbcType=VARCHAR}


      AND sale_price = #{salePrice, jdbcType=BIGINT}


      AND customer_name = #{customerName, jdbcType=VARCHAR}


      AND customer_phone = #{customerPhone, jdbcType=VARCHAR}


      AND retention_time = #{retentionTime, jdbcType=TIMESTAMP}


      AND follow_person = #{followPerson, jdbcType=BIGINT}


      AND follow_time = #{followTime, jdbcType=TIMESTAMP}


      AND clues_source = #{cluesSource, jdbcType=VARCHAR}


      AND follow_status = #{followStatus, jdbcType=VARCHAR}


      AND last_follow_time = #{lastFollowTime, jdbcType=TIMESTAMP}


      AND next_follow_time = #{nextFollowTime, jdbcType=TIMESTAMP}


      AND customer_character = #{customerCharacter, jdbcType=VARCHAR}


      AND purchase_time = #{purchaseTime, jdbcType=VARCHAR}


      AND family_situation = #{familySituation, jdbcType=VARCHAR}


      AND is_show = #{isShow, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

