mapper.delete:
DELETE FROM t_psi_sales_customer
WHERE

       AND customer_id = #{customerId, jdbcType=BIGINT}

DELETE FROM t_psi_sales_customer
WHERE


      AND customer_id = #{customerId, jdbcType=BIGINT}


      AND uid = #{uid, jdbcType=VARCHAR}


      AND store_id = #{storeId, jdbcType=BIGINT}


      AND party_id = #{partyId, jdbcType=BIGINT}


      AND customer_name = #{customerName, jdbcType=VARCHAR}


      AND customer_phone = #{customerPhone, jdbcType=VARCHAR}


      AND model_code = #{modelCode, jdbcType=VARCHAR}


      AND model_name = #{modelName, jdbcType=VARCHAR}


      AND delivery_place = #{deliveryPlace, jdbcType=VARCHAR}


      AND vin = #{vin, jdbcType=VARCHAR}


      AND follow_time = #{followTime, jdbcType=TIMESTAMP}


      AND follow_by = #{followBy, jdbcType=BIGINT}


      AND follow_name = #{followName, jdbcType=VARCHAR}


      AND customer_character = #{customerCharacter, jdbcType=VARCHAR}


      AND purchase_time = #{purchaseTime, jdbcType=VARCHAR}


      AND family = #{family, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

