      AND message_record_id = #{messageRecordId, jdbcType=BIGINT}


      AND template_id = #{templateId, jdbcType=VARCHAR}


      AND relevance_type = #{relevanceType, jdbcType=VARCHAR}


      AND message_type = #{messageType, jdbcType=VARCHAR}


      AND relevance_id = #{relevanceId, jdbcType=BIGINT}


      AND recipient = #{recipient, jdbcType=VARCHAR}


      AND param = #{param, jdbcType=VARCHAR}


      AND status = #{status, jdbcType=VARCHAR}


      AND reception_time = #{receptionTime, jdbcType=TIMESTAMP}

