mapper.delete:
DELETE FROM t_sys_jpush_record
WHERE

       AND jpush_id = #{jpushId, jdbcType=BIGINT}

DELETE FROM t_sys_jpush_record
WHERE


      AND jpush_id = #{jpushId, jdbcType=BIGINT}


      AND template_id = #{templateId, jdbcType=BIGINT}


      AND type = #{type, jdbcType=VARCHAR}


      AND relevance_id = #{relevanceId, jdbcType=BIGINT}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND request_param = #{requestParam, jdbcType=LONGVARCHAR}


      AND response_param = #{responseParam, jdbcType=LONGVARCHAR}

mapper.select:
SELECT 

  FROM t_sys_jpush_record a


      AND a.jpush_id = #{jpushId, jdbcType=BIGINT}


      AND a.template_id = #{templateId, jdbcType=BIGINT}


      AND a.type = #{type, jdbcType=VARCHAR}


      AND a.relevance_id = #{relevanceId, jdbcType=BIGINT}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.request_param = #{requestParam, jdbcType=LONGVARCHAR}


      AND a.response_param = #{responseParam, jdbcType=LONGVARCHAR}

SELECT count(1) FROM t_sys_jpush_record a


      AND a.jpush_id = #{jpushId, jdbcType=BIGINT}


      AND a.template_id = #{templateId, jdbcType=BIGINT}


      AND a.type = #{type, jdbcType=VARCHAR}


      AND a.relevance_id = #{relevanceId, jdbcType=BIGINT}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.request_param = #{requestParam, jdbcType=LONGVARCHAR}


      AND a.response_param = #{responseParam, jdbcType=LONGVARCHAR}
