mapper.delete:
DELETE FROM t_sys_jpush_template_config
WHERE

       AND template_id = #{templateId, jdbcType=BIGINT}

DELETE FROM t_sys_jpush_template_config
WHERE


      AND template_id = #{templateId, jdbcType=BIGINT}


      AND template_type = #{templateType, jdbcType=VARCHAR}


      AND category = #{category, jdbcType=VARCHAR}


      AND trigger_name = #{triggerName, jdbcType=VARCHAR}


      AND title = #{title, jdbcType=VARCHAR}


      AND content = #{content, jdbcType=VARCHAR}


      AND url = #{url, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_sys_jpush_template_config a


      AND a.template_id = #{templateId, jdbcType=BIGINT}


      AND a.template_type = #{templateType, jdbcType=VARCHAR}


      AND a.category = #{category, jdbcType=VARCHAR}


      AND a.trigger_name = #{triggerName, jdbcType=VARCHAR}


      AND a.title = #{title, jdbcType=VARCHAR}


      AND a.content = #{content, jdbcType=VARCHAR}


      AND a.url = #{url, jdbcType=VARCHAR}

SELECT count(1) FROM t_sys_jpush_template_config a


      AND a.template_id = #{templateId, jdbcType=BIGINT}


      AND a.template_type = #{templateType, jdbcType=VARCHAR}


      AND a.category = #{category, jdbcType=VARCHAR}


      AND a.trigger_name = #{triggerName, jdbcType=VARCHAR}


      AND a.title = #{title, jdbcType=VARCHAR}


      AND a.content = #{content, jdbcType=VARCHAR}


      AND a.url = #{url, jdbcType=VARCHAR}
