mapper.delete:
DELETE FROM t_sys_partner_details
WHERE

       AND partner_id = #{partnerId, jdbcType=BIGINT}

DELETE FROM t_sys_partner_details
WHERE


      AND partner_id = #{partnerId, jdbcType=BIGINT}


      AND partner_code = #{partnerCode, jdbcType=VARCHAR}


      AND partner_charge_person = #{partnerChargePerson, jdbcType=VARCHAR}


      AND charge_person_iphone = #{chargePersonIphone, jdbcType=VARCHAR}


      AND partner_name = #{partnerName, jdbcType=VARCHAR}


      AND partner_abbreviation = #{partnerAbbreviation, jdbcType=VARCHAR}


      AND partner_join_role = #{partnerJoinRole, jdbcType=VARCHAR}


      AND partner_status = #{partnerStatus, jdbcType=VARCHAR}


      AND partner_address = #{partnerAddress, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_sys_partner_details a


      AND a.partner_id = #{partnerId, jdbcType=BIGINT}


