      AND a.charge_person_iphone = #{chargePersonIphone, jdbcType=VARCHAR}


      AND a.partner_name = #{partnerName, jdbcType=VARCHAR}


      AND a.partner_abbreviation = #{partnerAbbreviation, jdbcType=VARCHAR}


      AND a.partner_join_role = #{partnerJoinRole, jdbcType=VARCHAR}


      AND a.partner_status = #{partnerStatus, jdbcType=VARCHAR}


      AND a.partner_address = #{partnerAddress, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_sys_partner_details a


      AND a.partner_id = #{partnerId, jdbcType=BIGINT}


