mapper.delete:
DELETE FROM t_sys_partner_staff_info
WHERE

       AND party_id = #{partyId, jdbcType=BIGINT}

DELETE FROM t_sys_partner_staff_info
WHERE


      AND party_id = #{partyId, jdbcType=BIGINT}


      AND partner_staff_name = #{partnerStaffName, jdbcType=VARCHAR}


      AND partner_staff_iphone = #{partnerStaffIphone, jdbcType=VARCHAR}


      AND partner_staff_sex = #{partnerStaffSex, jdbcType=VARCHAR}


      AND partner_staff_email = #{partnerStaffEmail, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_sys_partner_staff_info a


      AND a.party_id = #{partyId, jdbcType=BIGINT}


      AND a.partner_staff_name = #{partnerStaffName, jdbcType=VARCHAR}


      AND a.partner_staff_iphone = #{partnerStaffIphone, jdbcType=VARCHAR}


      AND a.partner_staff_sex = #{partnerStaffSex, jdbcType=VARCHAR}


      AND a.partner_staff_email = #{partnerStaffEmail, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_sys_partner_staff_info a


      AND a.party_id = #{partyId, jdbcType=BIGINT}


