mapper.delete:
DELETE FROM t_sys_region
WHERE

       AND region_code = #{regionCode, jdbcType=BIGINT}

DELETE FROM t_sys_region
WHERE


      AND region_code = #{regionCode, jdbcType=BIGINT}


      AND region_name = #{regionName, jdbcType=VARCHAR}


      AND region_level = #{regionLevel, jdbcType=BIT}


      AND parent_code = #{parentCode, jdbcType=BIGINT}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_sys_region a


      AND a.region_code = #{regionCode, jdbcType=BIGINT}


      AND a.region_name = #{regionName, jdbcType=VARCHAR}


      AND a.region_level = #{regionLevel, jdbcType=BIT}


      AND a.parent_code = #{parentCode, jdbcType=BIGINT}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_sys_region a


      AND a.region_code = #{regionCode, jdbcType=BIGINT}


      AND a.region_name = #{regionName, jdbcType=VARCHAR}


      AND a.region_level = #{regionLevel, jdbcType=BIT}


      AND a.parent_code = #{parentCode, jdbcType=BIGINT}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}
