mapper.delete:
DELETE FROM t_sys_store_staff_info
WHERE

       AND store_staff_id = #{storeStaffId, jdbcType=BIGINT}

DELETE FROM t_sys_store_staff_info
WHERE


      AND store_staff_id = #{storeStaffId, jdbcType=BIGINT}


      AND party_id = #{partyId, jdbcType=BIGINT}


      AND user_id = #{userId, jdbcType=BIGINT}


      AND store_id = #{storeId, jdbcType=BIGINT}


      AND idm_account_name = #{idmAccountName, jdbcType=VARCHAR}


      AND user_name = #{userName, jdbcType=VARCHAR}


      AND phone_number = #{phoneNumber, jdbcType=VARCHAR}


      AND role_code = #{roleCode, jdbcType=VARCHAR}


      AND role_name = #{roleName, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_sys_store_staff_info a


      AND a.store_staff_id = #{storeStaffId, jdbcType=BIGINT}


