      AND a.user_id = #{userId, jdbcType=BIGINT}


      AND a.store_id = #{storeId, jdbcType=BIGINT}


      AND a.idm_account_name = #{idmAccountName, jdbcType=VARCHAR}


      AND a.user_name = #{userName, jdbcType=VARCHAR}


      AND a.phone_number = #{phoneNumber, jdbcType=VARCHAR}


      AND a.role_code = #{roleCode, jdbcType=VARCHAR}


      AND a.role_name = #{roleName, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_sys_store_staff_info a


