mapper.select:
select

from t_psi_sales_modify_config psmc
where psmc.apply_id = #{applyId,jdbcType=BIGINT}

select

from t_psi_sales_modify_config psmc


        AND psmc.apply_id = #{applyId,jdbcType=BIGINT}


        AND psmc.order_info_id = #{orderInfoId,jdbcType=BIGINT}


        AND psmc.order_status = #{orderStatus,jdbcType=VARCHAR}


        AND psmc.vin = #{vin,jdbcType=VARCHAR}


        AND psmc.change_vin = #{changeVin,jdbcType=VARCHAR}


        AND psmc.apply_by = #{applyBy,jdbcType=BIGINT}


        AND psmc.apply_date = #{applyDate,jdbcType=TIMESTAMP}


        AND psmc.approve_by = #{approveBy,jdbcType=BIGINT}


        AND psmc.approve_result = #{approveResult,jdbcType=TINYINT}


        AND psmc.approve_date = #{approveDate,jdbcType=TIMESTAMP}


        AND psmc.remark = #{remark,jdbcType=VARCHAR}


        AND psmc.apply_source = #{applySource,jdbcType=TINYINT}

mapper.insert:
insert into t_psi_sales_modify_config (order_info_id,order_status, vin, change_vin,
                                       apply_by, apply_date, approve_by, approve_result,
                                       approve_date, remark, apply_source
)
values (#{orderInfoId,jdbcType=BIGINT}, #{orderStatus,jdbcType=VARCHAR}, #{vin,jdbcType=VARCHAR}, #{changeVin,jdbcType=VARCHAR},
        #{applyBy,jdbcType=BIGINT}, #{applyDate,jdbcType=TIMESTAMP}, #{approveBy,jdbcType=BIGINT},#{approveResult,jdbcType=TINYINT},
        #{approveDate,jdbcType=TIMESTAMP}, #{remark,jdbcType=VARCHAR}, #{applySource,jdbcType=TINYINT}
       )
