mapper.delete:
DELETE FROM t_psi_customer_cars
WHERE

       AND reservation_id = #{reservationId, jdbcType=BIGINT}

DELETE FROM t_psi_customer_cars
WHERE


      AND reservation_id = #{reservationId, jdbcType=BIGINT}


      AND customer_id = #{customerId, jdbcType=BIGINT}


      AND brand = #{brand, jdbcType=VARCHAR}


      AND brand_chinese_describe = #{brandChineseDescribe, jdbcType=VARCHAR}


      AND car_series = #{carSeries, jdbcType=VARCHAR}


      AND car_series_chinese_describe = #{carSeriesChineseDescribe, jdbcType=VARCHAR}


      AND car_year = #{carYear, jdbcType=VARCHAR}


      AND car_year_chinese_describe = #{carYearChineseDescribe, jdbcType=VARCHAR}


      AND car_type = #{carType, jdbcType=VARCHAR}


      AND car_type_chinese_describe = #{carTypeChineseDescribe, jdbcType=VARCHAR}


      AND licensing_city = #{licensingCity, jdbcType=VARCHAR}


      AND licensing_province = #{licensingProvince, jdbcType=VARCHAR}


      AND licensing_date = #{licensingDate, jdbcType=DATE}


      AND driving_mileage = #{drivingMileage, jdbcType=VARCHAR}


      AND color = #{color, jdbcType=VARCHAR}


      AND license_plate_num = #{licensePlateNum, jdbcType=VARCHAR}


      AND vin_code = #{vinCode, jdbcType=VARCHAR}


      AND transfer_times = #{transferTimes, jdbcType=VARCHAR}


      AND using_nature = #{usingNature, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND rank = #{rank, jdbcType=VARCHAR}


      AND revert_body = #{revertBody, jdbcType=VARCHAR}

