mapper.delete:
DELETE FROM t_psi_customer_reservation_msg
WHERE

       AND reservation_id = #{reservationId, jdbcType=BIGINT}

DELETE FROM t_psi_customer_reservation_msg
WHERE


      AND reservation_id = #{reservationId, jdbcType=BIGINT}


      AND reservation_detection_date = #{reservationDetectionDate, jdbcType=TIMESTAMP}


      AND reservation_detection_address = #{reservationDetectionAddress, jdbcType=VARCHAR}


      AND customer_expect_price = #{customerExpectPrice, jdbcType=BIGINT}


      AND order_receiving_deadline = #{orderReceivingDeadline, jdbcType=TIMESTAMP}


      AND vehicle_registration_file_id = #{vehicleRegistrationFileId, jdbcType=BIGINT}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

