mapper.delete:
DELETE FROM t_psi_order_receiving_record
WHERE

       AND record_id = #{recordId, jdbcType=BIGINT}

DELETE FROM t_psi_order_receiving_record
WHERE


      AND record_id = #{recordId, jdbcType=BIGINT}


      AND reservation_id = #{reservationId, jdbcType=BIGINT}


      AND record_type = #{recordType, jdbcType=VARCHAR}


      AND order_status = #{orderStatus, jdbcType=VARCHAR}


      AND reason = #{reason, jdbcType=VARCHAR}


      AND operation = #{operation, jdbcType=VARCHAR}


      AND operation_by = #{operationBy, jdbcType=BIGINT}


      AND operation_date = #{operationDate, jdbcType=TIMESTAMP}

