mapper.delete:
DELETE FROM t_rep_replacement_approval_record
WHERE

       AND approval_id = #{approvalId, jdbcType=BIGINT}

DELETE FROM t_rep_replacement_approval_record
WHERE


      AND approval_id = #{approvalId, jdbcType=BIGINT}


      AND replacement_id = #{replacementId, jdbcType=BIGINT}


      AND approval_result = #{approvalResult, jdbcType=VARCHAR}


      AND approval_remark = #{approvalRemark, jdbcType=VARCHAR}


      AND approval_person = #{approvalPerson, jdbcType=BIGINT}


      AND approval_person_name = #{approvalPersonName, jdbcType=VARCHAR}


      AND approval_date = #{approvalDate, jdbcType=TIMESTAMP}


      AND reporting_date = #{reportingDate, jdbcType=TIMESTAMP}


      AND replacement_before_status = #{replacementBeforeStatus, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_rep_replacement_approval_record a


      AND a.approval_id = #{approvalId, jdbcType=BIGINT}


      AND a.replacement_id = #{replacementId, jdbcType=BIGINT}


      AND a.approval_result = #{approvalResult, jdbcType=VARCHAR}


      AND a.approval_remark = #{approvalRemark, jdbcType=VARCHAR}


      AND a.approval_person = #{approvalPerson, jdbcType=BIGINT}


      AND a.approval_person_name = #{approvalPersonName, jdbcType=VARCHAR}


      AND a.approval_date = #{approvalDate, jdbcType=TIMESTAMP}


      AND a.reporting_date = #{reportingDate, jdbcType=TIMESTAMP}


      AND a.replacement_before_status = #{replacementBeforeStatus, jdbcType=VARCHAR}

