mapper.delete:
DELETE FROM t_rep_replacement_material
WHERE

       AND material_id = #{materialId, jdbcType=BIGINT}

DELETE FROM t_rep_replacement_material
WHERE


      AND material_id = #{materialId, jdbcType=BIGINT}


      AND replacement_id = #{replacementId, jdbcType=BIGINT}


      AND material_type = #{materialType, jdbcType=VARCHAR}


      AND upload_person = #{uploadPerson, jdbcType=BIGINT}


      AND upload_date = #{uploadDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_rep_replacement_material a


      AND a.material_id = #{materialId, jdbcType=BIGINT}


      AND a.replacement_id = #{replacementId, jdbcType=BIGINT}


      AND a.material_type = #{materialType, jdbcType=VARCHAR}


      AND a.upload_person = #{uploadPerson, jdbcType=BIGINT}


      AND a.upload_date = #{uploadDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_rep_replacement_material a


      AND a.material_id = #{materialId, jdbcType=BIGINT}


      AND a.replacement_id = #{replacementId, jdbcType=BIGINT}


      AND a.material_type = #{materialType, jdbcType=VARCHAR}


      AND a.upload_person = #{uploadPerson, jdbcType=BIGINT}


      AND a.upload_date = #{uploadDate, jdbcType=TIMESTAMP}
