mapper.delete:
DELETE FROM t_sys_right_activities
WHERE

       AND right_id = #{rightId, jdbcType=BIGINT}

DELETE FROM t_sys_right_activities
WHERE


      AND right_id = #{rightId, jdbcType=BIGINT}


      AND right_pack_id = #{rightPackId, jdbcType=BIGINT}


      AND campaign_name = #{campaignName, jdbcType=VARCHAR}


      AND campaign_type = #{campaignType, jdbcType=VARCHAR}


      AND campaign_describe = #{campaignDescribe, jdbcType=VARCHAR}


      AND campaign_code = #{campaignCode, jdbcType=VARCHAR}


      AND model_code = #{modelCode, jdbcType=VARCHAR}


      AND model_name = #{modelName, jdbcType=VARCHAR}


      AND shape_code = #{shapeCode, jdbcType=VARCHAR}


      AND shape_name = #{shapeName, jdbcType=VARCHAR}


      AND conditions = #{conditions, jdbcType=VARCHAR}


      AND start_date = #{startDate, jdbcType=TIMESTAMP}


      AND end_date = #{endDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_person_name = #{createdPersonName, jdbcType=VARCHAR}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

