mapper.delete:
DELETE FROM t_sys_store_info
WHERE

       AND store_id = #{storeId, jdbcType=BIGINT}

DELETE FROM t_sys_store_info
WHERE


      AND store_id = #{storeId, jdbcType=BIGINT}


      AND org_id = #{orgId, jdbcType=VARCHAR}


      AND org_code = #{orgCode, jdbcType=VARCHAR}


      AND org_name = #{orgName, jdbcType=VARCHAR}


      AND org_type = #{orgType, jdbcType=VARCHAR}


      AND org_type_name = #{orgTypeName, jdbcType=VARCHAR}


      AND parent_org_id = #{parentOrgId, jdbcType=VARCHAR}


      AND dlr_short_name = #{dlrShortName, jdbcType=VARCHAR}


      AND dlr_full_name = #{dlrFullName, jdbcType=VARCHAR}


      AND dlr_type = #{dlrType, jdbcType=VARCHAR}


      AND dlr_en_name = #{dlrEnName, jdbcType=VARCHAR}


      AND link_addr = #{linkAddr, jdbcType=VARCHAR}


      AND province_id = #{provinceId, jdbcType=VARCHAR}


      AND province = #{province, jdbcType=VARCHAR}


      AND city_id = #{cityId, jdbcType=VARCHAR}


      AND city = #{city, jdbcType=VARCHAR}


      AND area_code = #{areaCode, jdbcType=VARCHAR}


      AND area_name = #{areaName, jdbcType=VARCHAR}


      AND county_id = #{countyId, jdbcType=VARCHAR}


      AND sale_tel = #{saleTel, jdbcType=VARCHAR}


      AND dlr_status = #{dlrStatus, jdbcType=VARCHAR}


      AND online_time = #{onlineTime, jdbcType=VARCHAR}


      AND shut_down_date = #{shutDownDate, jdbcType=VARCHAR}


      AND lng = #{lng, jdbcType=VARCHAR}


      AND lat = #{lat, jdbcType=VARCHAR}


      AND business_time = #{businessTime, jdbcType=VARCHAR}


      AND is_drive = #{isDrive, jdbcType=VARCHAR}


      AND is_c_show = #{isCShow, jdbcType=VARCHAR}


      AND dlr_code = #{dlrCode, jdbcType=VARCHAR}


      AND order_no = #{orderNo, jdbcType=VARCHAR}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND remark = #{remark, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

