mapper.select:
SELECT

FROM t_sys_role_details a


        AND a.role_id = #{roleId, jdbcType=BIGINT}


        AND a.role_code = #{roleCode, jdbcType=VARCHAR}


        AND a.role_details_type like concat('%',#{roleDetailsType, jdbcType=VARCHAR},'%')


        AND a.role_details_name like concat('%',#{roleDetailsName, jdbcType=VARCHAR},'%')

SELECT a.resource_id AS resourceId, c.resource_content AS resourceContent, GROUP_CONCAT(CONCAT(a.operation_id, ':', b.operation_name, ':', a.permission_id)) AS operPermission FROM t_permission_info a, t_operation_info b, t_resource_info c, t_resource_mapping d WHERE a.operation_id = b.operation_id and a.resource_id = c.resource_id and a.resource_id = d.resource_id and (d.resource_type = #{type} or d.resource_type=3)
and a.resource_id = #{resourceId, jdbcType=VARCHAR}
GROUP BY a.resource_id, c.resource_content
