mapper.delete:
DELETE FROM t_application_info
WHERE

       AND application_id = #{applicationId, jdbcType=BIGINT}

DELETE FROM t_application_info
WHERE


      AND application_id = #{applicationId, jdbcType=BIGINT}


      AND application_name = #{applicationName, jdbcType=VARCHAR}


      AND application_path = #{applicationPath, jdbcType=VARCHAR}


      AND application_desc = #{applicationDesc, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_application_info a


      AND a.application_id = #{applicationId, jdbcType=BIGINT}


      AND a.application_name = #{applicationName, jdbcType=VARCHAR}


      AND a.application_path = #{applicationPath, jdbcType=VARCHAR}


      AND a.application_desc = #{applicationDesc, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_application_info a


      AND a.application_id = #{applicationId, jdbcType=BIGINT}


      AND a.application_name = #{applicationName, jdbcType=VARCHAR}


      AND a.application_path = #{applicationPath, jdbcType=VARCHAR}


      AND a.application_desc = #{applicationDesc, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


