mapper.delete:
DELETE FROM t_job_receive_info
WHERE

       AND receive_id = #{receiveId, jdbcType=BIGINT}

DELETE FROM t_job_receive_info
WHERE


      AND receive_id = #{receiveId, jdbcType=BIGINT}


      AND receive_type = #{receiveType, jdbcType=VARCHAR}


      AND job_type = #{jobType, jdbcType=VARCHAR}


      AND receive_task_type = #{receiveTaskType, jdbcType=VARCHAR}


      AND receive_count = #{receiveCount, jdbcType=INTEGER}


      AND receive_timestamp = #{receiveTimestamp, jdbcType=BIGINT}


      AND message_no = #{messageNo, jdbcType=VARCHAR}


      AND receive_result = #{receiveResult, jdbcType=VARCHAR}


      AND processing_status = #{processingStatus, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

