mapper.delete:
DELETE FROM t_login_info
WHERE

       AND login_id = #{loginId, jdbcType=BIGINT}

DELETE FROM t_login_info
WHERE


      AND login_id = #{loginId, jdbcType=BIGINT}


      AND party_id = #{partyId, jdbcType=BIGINT}


      AND login_name = #{loginName, jdbcType=VARCHAR}


      AND is_primary = #{isPrimary, jdbcType=CHAR}


      AND is_password = #{isPassword, jdbcType=CHAR}


      AND valid_mark = #{validMark, jdbcType=CHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_login_info a


      AND a.login_id = #{loginId, jdbcType=BIGINT}


      AND a.party_id = #{partyId, jdbcType=BIGINT}


      AND a.login_name = #{loginName, jdbcType=VARCHAR}


      AND a.is_primary = #{isPrimary, jdbcType=CHAR}


      AND a.is_password = #{isPassword, jdbcType=CHAR}


      AND a.valid_mark = #{validMark, jdbcType=CHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_login_info a


