mapper.delete:
DELETE FROM t_menu_info
WHERE

       AND menu_id = #{menuId, jdbcType=BIGINT}

DELETE FROM t_menu_info
WHERE


      AND menu_id = #{menuId, jdbcType=BIGINT}


      AND parent_menu_id = #{parentMenuId, jdbcType=BIGINT}


      AND menu_name = #{menuName, jdbcType=VARCHAR}


      AND identifier_id = #{identifierId, jdbcType=BIGINT}


      AND is_valid = #{isValid, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}


      AND menu_sequence = #{menuSequence, jdbcType=INTEGER}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_menu_info a


      AND a.menu_id = #{menuId, jdbcType=BIGINT}


      AND a.parent_menu_id = #{parentMenuId, jdbcType=BIGINT}


      AND a.menu_name = #{menuName, jdbcType=VARCHAR}


      AND a.identifier_id = #{identifierId, jdbcType=BIGINT}


      AND a.is_valid = #{isValid, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}


      AND a.menu_sequence = #{menuSequence, jdbcType=INTEGER}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_menu_info a


      AND a.menu_id = #{menuId, jdbcType=BIGINT}


