mapper.delete:
DELETE FROM t_party_permission_rela
WHERE

       AND party_permission_id = #{partyPermissionId, jdbcType=BIGINT}

DELETE FROM t_party_permission_rela
WHERE


      AND party_permission_id = #{partyPermissionId, jdbcType=BIGINT}


      AND party_id = #{partyId, jdbcType=BIGINT}


      AND permission_id = #{permissionId, jdbcType=BIGINT}


      AND permission_wildcards = #{permissionWildcards, jdbcType=VARCHAR}


      AND effective_date = #{effectiveDate, jdbcType=TIMESTAMP}


      AND expiry_date = #{expiryDate, jdbcType=TIMESTAMP}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

