mapper.delete:
DELETE FROM t_party_relationship
WHERE

       AND relationship_id = #{relationshipId, jdbcType=BIGINT}

DELETE FROM t_party_relationship
WHERE


      AND relationship_id = #{relationshipId, jdbcType=BIGINT}


      AND relationship_type_code = #{relationshipTypeCode, jdbcType=VARCHAR}


      AND src_party_id = #{srcPartyId, jdbcType=BIGINT}


      AND tag_party_id = #{tagPartyId, jdbcType=BIGINT}


      AND start_date = #{startDate, jdbcType=DATE}


      AND end_date = #{endDate, jdbcType=DATE}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_party_relationship a


      AND a.relationship_id = #{relationshipId, jdbcType=BIGINT}


      AND a.relationship_type_code = #{relationshipTypeCode, jdbcType=VARCHAR}


      AND a.src_party_id = #{srcPartyId, jdbcType=BIGINT}


      AND a.tag_party_id = #{tagPartyId, jdbcType=BIGINT}


      AND a.start_date = #{startDate, jdbcType=DATE}


      AND a.end_date = #{endDate, jdbcType=DATE}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_party_relationship a


      AND a.relationship_id = #{relationshipId, jdbcType=BIGINT}


      AND a.relationship_type_code = #{relationshipTypeCode, jdbcType=VARCHAR}


      AND a.src_party_id = #{srcPartyId, jdbcType=BIGINT}


      AND a.tag_party_id = #{tagPartyId, jdbcType=BIGINT}


      AND a.start_date = #{startDate, jdbcType=DATE}


      AND a.end_date = #{endDate, jdbcType=DATE}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}
