mapper.delete:
DELETE FROM t_permission_info
WHERE

       AND permission_id = #{permissionId, jdbcType=BIGINT}

DELETE FROM t_permission_info
WHERE


      AND permission_id = #{permissionId, jdbcType=BIGINT}


      AND resource_id = #{resourceId, jdbcType=BIGINT}


      AND operation_id = #{operationId, jdbcType=BIGINT}


      AND permission_name = #{permissionName, jdbcType=VARCHAR}


      AND permission_desc = #{permissionDesc, jdbcType=VARCHAR}


      AND is_valid = #{isValid, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_permission_info a


      AND a.permission_id = #{permissionId, jdbcType=BIGINT}


      AND a.resource_id = #{resourceId, jdbcType=BIGINT}


      AND a.operation_id = #{operationId, jdbcType=BIGINT}


      AND a.permission_name = #{permissionName, jdbcType=VARCHAR}


      AND a.permission_desc = #{permissionDesc, jdbcType=VARCHAR}


      AND a.is_valid = #{isValid, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_permission_info a


      AND a.permission_id = #{permissionId, jdbcType=BIGINT}


