      AND a.sex_type_code = #{sexTypeCode, jdbcType=VARCHAR}


      AND a.birth_date = #{birthDate, jdbcType=DATE}


      AND a.marital_type_code = #{maritalTypeCode, jdbcType=VARCHAR}


      AND a.person_memo = #{personMemo, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

