mapper.delete:
DELETE FROM t_position_info
WHERE

       AND party_id = #{partyId, jdbcType=BIGINT}

DELETE FROM t_position_info
WHERE


      AND party_id = #{partyId, jdbcType=BIGINT}


      AND position_name = #{positionName, jdbcType=VARCHAR}


      AND position_memo = #{positionMemo, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_position_info a


      AND a.party_id = #{partyId, jdbcType=BIGINT}


      AND a.position_name = #{positionName, jdbcType=VARCHAR}


      AND a.position_memo = #{positionMemo, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_position_info a


      AND a.party_id = #{partyId, jdbcType=BIGINT}


      AND a.position_name = #{positionName, jdbcType=VARCHAR}


      AND a.position_memo = #{positionMemo, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}
