mapper.delete:
DELETE FROM t_psi_car_transfer_apply
WHERE

       AND transfer_apply_id = #{transferApplyId, jdbcType=BIGINT}

DELETE FROM t_psi_car_transfer_apply
WHERE


      AND transfer_apply_id = #{transferApplyId, jdbcType=BIGINT}


      AND stock_id = #{stockId, jdbcType=BIGINT}


      AND warehous_date = #{warehousDate, jdbcType=TIMESTAMP}


      AND storage_id = #{storageId, jdbcType=BIGINT}


      AND transfer_storage_id = #{transferStorageId, jdbcType=BIGINT}


      AND start_time = #{startTime, jdbcType=TIMESTAMP}


      AND expected_time = #{expectedTime, jdbcType=TIMESTAMP}


      AND transfer_type = #{transferType, jdbcType=VARCHAR}


      AND marks = #{marks, jdbcType=VARCHAR}


      AND transfer_status = #{transferStatus, jdbcType=VARCHAR}


      AND initiator = #{initiator, jdbcType=BIGINT}


      AND initiator_time = #{initiatorTime, jdbcType=TIMESTAMP}


      AND feedback_estimated_arrival_time = #{feedbackEstimatedArrivalTime, jdbcType=TIMESTAMP}


      AND delivery_time = #{deliveryTime, jdbcType=TIMESTAMP}


      AND is_submit = #{isSubmit, jdbcType=VARCHAR}


      AND dispatch_number = #{dispatchNumber, jdbcType=VARCHAR}


      AND instruction_number = #{instructionNumber, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

